import React, {Component} from 'react';

class Option extends Component {
    render() {
        return (
            <div>
                <li>{this.props.opText}</li>
                <button onClick={this.props.handleRemoveSingleOption}>Remove</button>
            </div>

        )
    }
}

export default Option;
